// =============================================================================
// Stencil Checkout  - Customize the checkout experience
// =============================================================================
//
// This file is for checkout customizations for Default one-page checkout.
//
// -----------------------------------------------------------------------------



.blocker {
  position: fixed;
  top: 0; right: 0; bottom: 0; left: 0;
  width: 100%; height: 100%;
  overflow: auto;
  z-index: 1;
  padding: 20px;
  box-sizing: border-box;
  text-align: center;
  background: rgba(0, 0, 0, .3);
}
.blocker:before{
  content: "";
  display: inline-block;
  height: 100%;
  vertical-align: middle;
  margin-right: -0.05em;
}

.blocker.behind {
  background-color: transparent;
}

.couponmodal {
  display: none;
  vertical-align: middle;
  position: relative;
  z-index: 2;
  max-width: 550px;
  box-sizing: border-box;
  width: 90%;
  background: #fff;
  padding: 60px;
  box-shadow: 0 0 1px #000;
  text-align: center;
  font-family: Roboto, sans-serif;
  font-size: 18px;
  font-weight: 300;
}

.couponmodal a {
    color: inherit;
}

.couponmodal h2 {
    font-size: 32px;
    font-weight: 800;

}

.couponmodal a.close-couponmodal {
  position: absolute;
  top: 10px;
  right: 10px;
  display: block;
  width: 16px;
  height: 16px;
  text-indent: -9999px;
  background-size: contain;
  background-repeat: no-repeat;
  background-position: center center;
  background-image: url('data:image/png;base64,');
}

.couponmodal-spinner {
  display: none;
  position: fixed;
  top: 50%;
  left: 50%;
  transform: translateY(-50%) translateX(-50%);
  padding: 12px 16px;
  border-radius: 5px;
  background-color: #111;
  height: 20px;
}

.couponmodal-spinner > div {
  border-radius: 100px;
  background-color: #fff;
  height: 20px;
  width: 2px;
  margin: 0 1px;
  display: inline-block;

  -webkit-animation: sk-stretchdelay 1.2s infinite ease-in-out;
  animation: sk-stretchdelay 1.2s infinite ease-in-out;
}

.couponmodal-spinner .rect2 {
  -webkit-animation-delay: -1.1s;
  animation-delay: -1.1s;
}

.couponmodal-spinner .rect3 {
  -webkit-animation-delay: -1.0s;
  animation-delay: -1.0s;
}

.couponmodal-spinner .rect4 {
  -webkit-animation-delay: -0.9s;
  animation-delay: -0.9s;
}

@-webkit-keyframes sk-stretchdelay {
  0%, 40%, 100% { -webkit-transform: scaleY(0.5) }
  20% { -webkit-transform: scaleY(1.0) }
}

@keyframes sk-stretchdelay {
  0%, 40%, 100% {
    transform: scaleY(0.5);
    -webkit-transform: scaleY(0.5);
  }  20% {
    transform: scaleY(1.0);
    -webkit-transform: scaleY(1.0);
  }
}


.couponbanner {
    width: 100%;
    align-items:center;
    justify-content:center;
}

.couponbanner-inner{
    width: 1280px;
    min-height: 40px;
    margin: 0px auto;
    display: flex;
    justify-content: center;
    align-items: center;
    font-family: Roboto, sans-serif;
    font-size: 16px;
    font-weight: 300;
    @media (max-width: 1280px){
        width: 100%;
        padding-left: 8px;
        padding-right: 8px;
        .coupon-code svg {
            display: none;
        }
    }
    @media (max-width: 800px){
        width: 100%;
        padding-left: 8px;
        padding-right: 8px;
        .coupon-code svg {
            display: none;
        }
        display: block;
        text-align: center;
    }    
}

.couponbanner-text{
    display: inline-block;
}

.couponbanner-text p {
    font-size: 16px;
    font-family: Roboto, sans-serif;
    margin: 0px;
} 


.couponbanner-code {
    display: inline-block;
    margin-left: 12px;
}

.coupon_code {
    padding: 6px;
    display: inline-block;
    font-size: 20px;
}

.couponbanner-code {
    padding-left: 0px;
    padding-right: 10px;
    padding-top: 3px;
    padding-bottom: 3px;
    display: inline-block;
    font-size: 16px;
}

.coupon_code svg {
    margin-left: 5px;
    vertical-align: text-top;
  width: 20px;
  height: 20px;    
}

.couponbanner-code svg {
    vertical-align: text-top;
  width: 16px;
  height: 16px;    
}

.coupon_code input, .couponbanner-code input, .coupon_code input:focus, .couponbanner-code input:focus {
    border: 0px;
    background: inherit;
    color: inherit;
    text-align: center;
    font-size: 16px;
    cursor: pointer;
    outline: none;
    width: 100px;
    font-weight: bold;
}

.coupon_code input,  .coupon_code input:focus,  {
    border: 0px;
    background: inherit;
    color: inherit;
    text-align: center;
    font-size: 28px;
    cursor: pointer;
    outline: none;
    width: 100px;
    font-weight: bold;
}
