pages/blog:
---
blog:
    posts:
        limit: 5
        pages: 3
        summary: 200
---
{{#partial "page"}}

{{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}

<main class="page">
  <div class="column">
    {{#unless theme_settings.hide_blog_page_heading }}
        <h1 class="page-heading">{{ blog.name }}</h1>
    {{/unless}}

    {{#each blog.posts}}
        {{> components/blog/post post=this postURL=(encodeURI url)}}
    {{/each}}

    {{> components/common/paginator pagination.blog}}
  </div>
</main>

{{/partial}}
{{> layout/base}}
