                      <img src="/product_images/uploaded_images/{{theme_settings.UspImageSecond}}" alt="">
                    </div>
                  {{/if}}
                  {{else}}
                    <div class="usp-img">
                        <img src="{{cdn 'assets/img/usp-icon/'}}{{theme_settings.selected-icon2}}.png" alt="">
                    </div>
