pages/page:
{{#partial "page"}}

{{> components/common/breadcrumbs breadcrumbs=breadcrumbs}}

{{#unless theme_settings.hide_page_heading }}
    <h1 class="page-heading">{{ page.title }}</h1>
{{/unless}}

<main class="page">

    {{#if pages}}
      <aside class="page-sidebar">
        {{> components/psdc-common/page-sidebar-block}}
      </aside>
    {{/if}}



    <div class="page-content page-content--centered">

        {{{region name="page_builder_content"}}}

        {{#replace '%%Syndicate%%' page.content}}
            {{> components/page/rss_feed}}
        {{else}}
            <p>{{{page.content}}}</p>            
        {{/replace}}
    </div>

    {{#if page.feed}}
        <ul>
            {{#each page.feed}}
                <li>
                    <h6><a href="{{url}}" target="_blank">{{title}}</a></h6>
                    <p>{{content}}</p>
                </li>
            {{/each}}
        </ul>
    {{/if}}
</main>

{{/partial}}

{{> layout/base}}
